--
-- Tablo için tablo yapısı `cicekler`
--

CREATE TABLE IF NOT EXISTS `cicekler` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `adi` text COLLATE utf8_turkish_ci NOT NULL,
  `ozellikler` text COLLATE utf8_turkish_ci NOT NULL,
  `turid` int(11) NOT NULL,
  `resimyolu` varchar(81) COLLATE utf8_turkish_ci NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB  DEFAULT CHARSET=utf8 COLLATE=utf8_turkish_ci AUTO_INCREMENT=22 ;

--
-- Tablo döküm verisi `cicekler`
--

INSERT INTO `cicekler` (`id`, `adi`, `ozellikler`, `turid`, `resimyolu`) VALUES
(1, 'ATEŞ ÇİÇEĞİ', 'Ballıbabagillerden bir yıllık otsu bitkidir. Anayurdu Güney Amerika’ dır. Dünyanın hemen her bölgesinde, bu arada Türkiye’de de park ve bahçelerde süs bitkisi olarak yetiştirilir. Yaprakları yumurtamsı, genişçe, sivri uçlu ve kenarları dişlidir. Yazın açan çiçekleri, genellikle genç sürgünlerin uç kısımlarında salkımlar biçiminde toplanmıştır. Bitki adını, çiçeklerinin kımızı renginden alır.Besince zengin, nemli ama suyu iyi akıntılı (süzek) bahçe topraklarını ve bol güneşli ortamları seven ateşçiçekleri, hafif gölge yerlerde de iyi gelişir. Bitki makul düzeyde sulanır. Ama, yazın sıcak ve kurak havalarda bitkiye verilen su miktarı artırılmalıdır. Ateşçiçekleri tohumuyla çoğaltılır. İlkbaharda 1618 derece sıcaklıklarda sıcak yastıklara ekilen tohumları 14 günde çimlenir. Bitkinin fideleri yeterince gelişince ve toprak ısındığında, bu fideler bahçelerdeki yerlerine şaşırtılarak sıra üzerinde 30''ar cm. aralık bırakılarak dikili', 1, 'resim/ates.jpg'),
(2, 'BEGONYA', 'Çiçekli begonyalar çok geniş bir aileye sahiptir. Öyle ki bu sayı binlerle ifade edilebilir. Kalıcı olanların yanı sıra soğanlı veya köklü geçici begonyalar da vardır. Hemen her renkte  açarlar. Özellikle soğanlıların çiçekleri son derece gösterişlidir. Bazıları kase büyüklüğünde gül biçimi katmerli çiçeklere sahiptir. Soğanlar ilkbaharda  dikilir. Sonbaharda bitki kuruduktan sonra kuru toprak içinde saklanır. Bu tür balkon ve bahçeye de dikilebilir. Köklü olan geçici bir tür de kışın açar.\r\nKalıcı begonyaları hepimiz iyi tanırız. Hemen her evde yetişen iri damla biçimi,genelde benekli yaprakları,salkımlar halinde pembe veya kırmızı çiçekleri olan kalıcı begonyalar metrelerce boylanabilirler.', 1, 'resim/begonya.jpg'),
(3, 'HERCAİ MENEKŞE', 'Menekşegiller familyasındandır. Anayurdu kesin olarak bilinmeyen hercai menekşelerin melez türleri ilk kez Avrupa''da üretilmiş, oradan çiçekler dünyasına tanıtılarak sevdirilmişlerdir. 20 cm. kadar boylanabilen hercai menekşeler çok yıllık ya da iki yıllık bitkiler olmalarına karşın, bir yıllık işlemi görürler. Ucuna doğru sivrilen oval biçimli, kenarları dişli yaprakları yeşil renklidir. Hafif ama hoş kokulu çiçekleri ilkbahardan başlayıp serin yerlerde yaz mevsimi boyunca açar. Bu çiçeklerde dördü aşağı, biri yukarı doğru bakan beş iri ve güzel taç yaprağı bulunur. Doğal hercai menekşeler sarı renkte çiçek açarken kültür türlerinde beyaz, mor, mavi ve koyu kırmızı\r\nrenklerde, üzeri düz ya da çizgili veya lekeli çiçek açan örnekleri de elde edilmiştir. Kentlerin kirli havasına kolayca uyum sağlayan hercai menekşeler, bahçe ve parklardaki çiçek tarhlarında yetiştirildiğinde çok süsleyici oldukları gibi, saksı ve plastik kutularda yetiştirilerek de pek hoş pencere önü ve özellikle balkon bitkisi özelliği kazanırlar.', 1, 'resim/hercai.jpg'),
(4, 'ROZET', 'Zakkumgiller familyasındandır. Bilimsel adındaki Catharanthus sözcüğü ''kusursuz çiçek'' anlamına gelen bitkinin anayurdu Madagaskar adası ile Hindistan''ın tropikal bölgeleridir. 45-50 cm. kadar boylanabilen rozet çiçeği çalımsı bir bitkidir. 2,5-7,5 cm. uzunlukta koyu yeşil renkli, derin damarlı parlak ayali yaprakları vardır. Mayıs ayında başlayıp sonbahardaki ilk donlara kadar sürekli çiçek açan bitkinin türlerine göre beş taçyapraklı 2,5-4 cm. genişlikteki çiçeklerinin renkleri beyaz, pembenin çeşitli tonları ile kırmızı renkte olur. Bu çiçekler düz renkli ya da çoğunlukla ortasında göz gibi görünen tek lekelidir. Sıcak ve bol güneşli yerlerde toprağı iyice kaplamak üzere\r\nyetiştirilen, çiçek tarhı ile bordürlerin vazgeçilmez bitkisi olan rozet çiçeği, saksı ya da plastik kutular içine yerleştirilerek evlerde pencere önü ve balkonlarda da çok iyi gelişir; ayrıca sarkan sepet saksılarda da çok hoş durur.', 1, 'resim/rozet.jpg'),
(5, 'HOROZ İBİĞİ', 'horoz ibiğigiller familyasının örnek bitkileridir. Anayurdu Asya anakarasının tropikal bölgeleri olan bir yıllık horoz ibiği bitkilerinin iki önemli türü bulunmaktadır: Bunlardan C.cristata 2030 cm. kadar boylanabilir. Çok dallı sürgünleri dikine gelişir. Geniş şeridimsi ya da oval biçimli yapraklan vardır. Yaz aylarında genellikle kırmızı renkte, bazen sarı ya da beyaz renkte açan çiçekleri toplu haldedir ve horozların ibiğine benzer bir görüntü oluşturur. 90 cm. kadar boylanabilen C.plumosa türü horozibiklerinin ise dikine gelişen kırmızımsı bir gövdesi ve sarımsı yeşil renkli oval biçimli yaprakları vardır. Gene yaz mevsiminde açan ve kırmızı, sarı, mercan ya da gül rengi olan çiçekleri devekuşu tüylerinin biçimini andırır. Horozibikleri, ilginç kesme çiçeklerdendir. Bu çiçekler gölge ve havadar bir yerde özenle kurutulup kışlık çiçek düzenlemelerinde kullanılır. horoz ibiği bitkileri bahçelerdeki çiçek tarhlarında güzel bir görüntü oluşturur. Haziran - temmuz aylarında 1520 cm. uzunlukta salkım ya da seyrek başaklar şeklinde açan çiçekleri sarı, beyaz, mavi ya da kırmızı renklerde ve pek gösterişli olur. Yüksek boyuyla bahçede duvarlarının veya çitlerin önüne çok yakışan bu bitkinin uzun ömürlü kesme çiçekleri, çiçek düzenlemelerine özellikle yeğlenerek konulmaktadır.', 1, 'resim/horoz.jpg'),
(6, 'KADİFE', 'Kadife Çiçeği, bahçelerde ve parklarda süs bitkisi olarak yetiştirilir. Yetiştirilmesi en kolay açık alan süs bitkilerindendir. Mevsimlik olarak yetiştirilmektedir. Çok fazla güneş alan balkonlar ve bahçelerde yetiştirilmesi uygundur. İlkbaharın sonlarına doğru çiçek açmaya başlayıp, sonbaharın soğukları başlayana kadar devam eder.\r\nKadife Çiçeği, Asteraceae ailesinin bir üyesidir. Bakımı kolaydır. Her türlü toprakta yetiştirilebilir. Hemen hemen her iklime uygundur. Tek dikkat etmeniz gereken şey, iyi güneş almasını sağlamalısınız ve susuz bırakmamalısınız. Bunlara dikkat etmeniz halinde herhangi bir sıkıntı yaşamayacaktır.\r\nTohum ile çoğaltılabilir. İlkbahar aylarında ekeceğiniz tohum kolaylıkla çimlenir ve hızlıca gelişir.', 1, 'resim/kadife.jpg'),
(7, 'KASIMPATI', 'Kasımpatları kasvetli ve çiçeksiz sonbahar bahçelerini canlı renkleriyle adeta bir yaz şölenine çevirirler.Uzak Doğu kültüründe önemli bir yeri olan bu çiçek bizde de öteden beri çok sevilir.\r\nBitki birbirinden farklı yüzlerce çeşide sahiptir.Kalıcı ve yıllık türleri vardır.Yetiştirilmesi gayet kolaydır.Genellikle çeliklerinden veya kök sürgünlerinden çoğaltılır.Tohumla yetiştirilen cinsleri de vardır.\r\nBahçede güneşli ve rüzgar almayan bir yere dikilir.Bitki yaz boyunca düzenli sulanır ve beslenir.Sonbaharda uzun boylu ve iri çiçekli olanları desteklenir.Çiçekler bittikten sonra bitki toprak hizasında kesilir.Üzerine biraz kuru ot veya gübre konursa kökler soğuktan etkilenmez.\r\nSaksıda yetiştirmek için küçük çiçekli ve kısa boylu olanları daha uygundur.Çoğaltmak isteniyorsa İlkbaharda bitki sürmeye başlayınca kök sürgünleri dikkatle ayrılıp dikilir.\r\nKasımpatı suda haftalarca dayanır.Topladığınız veya satın aldığınız kasımpatlarının iyice açmış olmasına dikkat edin.Tomurcuklar suda açmayabilir.Vazo suyuna konacak birkaç damla çamaşır suyu bakterial gelişimi önleyecektir.\r\n', 1, 'resim/kasimpati.jpg'),
(8, 'KARANFİL', 'Karanfiller (bahçe karanfilleri) karşılıklı, ensiz, sivri yapraklı, düğüm düğüm ince saplı otsu bitkilerdir. Dalcıkların ucunda tek tek yada topluca bulunan çiçekleri beyaz, pembe yada kırmızı renklidir. Her çiçek bir çanakçık oluşturan dört burgu yaprakçığıyla belirgindir.\r\nBahçe karanfili en ünlüsüdür. Bu karanfilin katmerli, yarı katmerli, alacalı ve hoş kokulu pek çok çeşidi vardır. Çok yıllık bir bitki olan bu karanfil türü kesme çiçek elde etmek için özellikle seralarda yetiştirilir ve çelikle üremesi sağlanır. Bahçe çeşitleri genellikle fideyle çoğaltılır ve iki yıllık yada çok yıllık bitkiler gibi yetiştirilir. Kır karanfili (dianthus plumarius) çim gibi sık biten, çok zarif küçük çiçekli ve ince saçaklı taç yapraklıdır. İki yıllık yada yıllık olan Çin karanfili (diantus sinensis) çok değişik çiçekli bir bitkidir. İki yıllık yada çok yıllık bir karanfil türü olan kıllı karanfil yada Hüsnü Yusuf olarak da bilinen (diantus barbatus) sap ucunda şemsiye biçimde toplu küçük çiçekler açan bir türdür.', 1, 'resim/karanfil.jpg'),
(9, 'SÜSEN', 'Süsengiller (İridaceae) ailesine ait çeşitli renklerde olabilen bitkilerdir. 80den fazla cinsi bulunan familya, tüm dünya çapında yaklaşık 1500 türe sahiptir. Türkiye’nin çoğu yerinde doğal olarak yetişir. Nisan haziran ayları arasında, hoş kokulu ve \r\ngüzel çiçekler açan 20-100cm boylarında, çok yıllık, otsu bitkilerdir. Etli ve \r\nsürünücü rizomları veya soğanları vardır. Yaprakları buğday yaprağına benzer düz veya kıvrık,damarlar paraleldir.\r\nÇiçekler altı parçalı, ucunda genellikle tek tek bulunurlar. Beyaz, sarı, mavi, mor renktedirler. Dıştaki taç yapraklar genellikle arkaya kıvrık olur.\r\nÇok değişik renklerde olan süsen bitkisi genellikle mezarlıklarda bulunmasına rağmen yüzyıllar boyunca çok değerli bir çiçek olarak kabul edilmiş ve birçok devlet ile kişinin simgesi haline gelmiştir. Günümüzde genellikle süs bitkisi olarak kullanılmaktadır.', 2, 'resim/susen.jpg'),
(10, 'SARDUNYA', 'Sardunya,turnagagasıgiller familyasındandır. Anayurdu Güney Afrika olan çok yıllık bitki. Akdeniz ülkelerinde o kadar çok yetiştirilir ki Akdeniz’le özdeşleştirilmiştir. Kışa dayanıklıdır. Pembe,sarı beyaz ve kırmızı tonlarında çiçekleri vardır.\r\nEşeysiz ürer ve ekilen küçük bir parçadan büyüyebilir. Genel, sakız sardunyası, ceylan ve ıtır gibi türleri vardır. Sıcağı sever ve çok çiçek açması için bol güneş görmelidir. Gelişmesi için neme lüzum yoktur.Ayrıca bu çiçek kızıl toprakta daha hızlı büyüyebilir.', 2, 'resim/sardunya.jpg'),
(11, 'MOR SALKIM', 'Baklagillerden, salkım durumunda mavi, mor, beyaz, pembe renkli,salkım şeklinde çiçekler açan, boyu 20 m''ye kadar uzayabilen çok yıllık bir sarmaşık (Wisteria sinensis) türüdür. Anayurdu Çin''dir.\r\nÖmrü yaklaşık 70 yıldır. . Bahçelerde süs bitkisi olarak yetiştirilir. Türkiye''de de bulunur.\r\nilkbahar’dan yaza geçişin mis kokulu habercisidir. Küçük bir fide İken, iki üç sene sonra çiçek açmaya başlar. İster ufak bir ağaç, ister müthiş bir sarmaşık olarak yetişebilen, Papilionaceae ailesinden bu bitkinin anavatanı, Uzakdoğu’da Çin ve Japonya’dır.Ama güneşli ya da yarı aydınlık (günde dört beş saat güneş alan yerler) ortamlarda yetişir. Malzemelerle desteklenmez ise zamanla yer örtücü bitkiler gibi gelişebilir.', 2, 'resim/morsalkim.jpg'),
(12, 'MELİSA(KOLONYA ÇİÇEĞİ)', 'Melisa çiçeği, parfüm çiçeği , kolonya çalısı veya kolonya çiçeği gibi isimler de verilen, muhtemelen en kuvvetli parfüme sahip Cestrum nocturnum 3,5 – 4 m ye kadar boylanabilen, tropik veya subtropik bölgelerde yetişen herdem yeşil bir çalıdır. Hava sıcaklığı yeterli olmazsa yapraklarını döküyor. Çok açık yeşil tubuler çiçekleri var.Melisa çiçeğinin ilginç özelliği akşamları minik çiçeklerinin güneşin batmasından sonra açılması ve çok  güzel bir koku yayması. Çiçekler sabah güneşin doğuşuyla kapanır ve koku yayması da duruyor.\r\nMelisa çiçekleri geceleri çok yoğun bir koku veriyor. Bitki aşırı rüzgara hassas. Birçok hibridi var. Hafif gölgeli ve korunaklı alanlarda daha başarılı sonuç veriyor. Tohumla, daldırma ile veya yeşil tepe çeliğiyle üretiliyor', 2, 'resim/melisa.jpg'),
(13, 'JAPON GÜLÜ', 'Japon gülü ya da diğer adıyla Çin gülü (Hibiscus rosa-sinensis), ebegümecigiller (Malvaceae) familyasından Doğu Asya kökenli saksı ve bahçe süs bitkisi türü.\r\nLatince adı "Çin gülü" anlamına gelse de Türkçede kullanılan "Japon gülü" adı çiçekçilerde ve halk arasında çok yaygındır. Ankara ve İstanbul gibi bölgelerde saksılarda yetiştirilen bitki, Akdeniz ve Ege sahillerinde bahçelerde yaygın biçimde görülür.Saksıda boyu kısa olsa da, bahçede 3 m.''ye kadar boylanabilen, 2 m.''ye kadar yayılma alanı gösterebilen, yerden sık dallanan, çalı formunda bitkilerdir. Gövde genç sürgünlerde, yeşil renkli yaşlı dalları gri renklidir. Yapraklar parlak yeşil renkli yumurta şeklindedir. Çiçekler uzun saplı, çok göz alıcı, iri, beyaz ve kırmızı başta olmak üzere çok farklı renklerde olabilir.', 2, 'resim/japon.jpg'),
(14, 'ASLAN AĞZI', 'Aslanağzı doğal olarak Kuzey Yarımküre''de ve özellikle de Akdeniz ve havzasında kendiliğinden yetişebilen,çiçekli bir bitkidir. \r\nBoyu 70-80 santime kadar çıkabilen aslanağzı; sarı, kırmızı, beyaz, mor ve pembenin tüm tonlarında salkım biçiminde açan çiçekleri olan, yaprakları oval mızrak biçiminde bulunan hoş bir çiçektir.Aslanağzı bakımı kolay ve güneş seven bir çiçek olduğundan dolayı evlerimizin balkonunda ve bahçelerimizde yetiştirilmeye oldukça uygun, hoş görünümlü bir bitkidir. Çok suyu sevmez ve toprak ayırt etmez. Dolayısıyla her gün bitkileriyle ilgilenemeyecek çiçek severlerin sıkça tercih edebileceği bir bitkidir. ', 2, 'resim/aslan.jpg'),
(15, 'ZAMBAK', 'Zambak (Liliaceae) familyasının Lilium cinsinden genellikle soğan \r\nile üreyen mevsimlik çiçekli bitkilerin adıdır. Zambakgiller familyasında \r\nbu cinse ait 110 civarında bitki türü vardır. Genellikle bahçe ve süs\r\nbitkisi olarak kullanılır, bazı soğanlı türleri de insanlar tarafından\r\nyenilebilir. Bu cinse ait zambak türü asıl zambaktır, isminde zambak geçen\r\nbaşka bitkilerde olmasına rağmen onlar diğer gruplara aittir. Zambak \r\nbitkisinin çiçekleri kimi ülke mutfaklarında besin olarak tüketilmektedir.\r\nKıs aylarında soğuktan fazla etkilenmemesi için çok sıcak olmayan yerlerde muhafaza edilmeli ve saksının üst boğumuna kadar kül veya kum koyarak soğuktan korunmalıdır. Hızlı şekilde zambak soğanları filizlenip mayıs aylarına doğru çiçek açıp balkonları veya bahçeleri renklendirecektir. ', 3, 'resim/zambak.jpg'),
(16, 'SÜMBÜL', 'Sümbüllerin anayurdunu doğuda İran ve Türkmenistan''a kadar dağılım gösteren doğu Akdeniz bölgesi oluşturmaktadır. Çok güzel kokan sümbül bitkisi çoğu mevsimde ayakta kalan çiçekleri ve kokusuyla ünlü soğanlı bir bitkidir.\r\nSümbül gibi soğanlı bitkileri ekerken bazı noktalara özellikle dikkat etmek gerekir. Sümbül soğanı,büyüklüğünün en fazla 2,5 katı kadar derine ekilmelidir. Toprağının kumlu ve gevşek yapıda olması gerekir. Ayrıca toprağı kurudukça yeteri kadar sulanmasına özen gösterilmeli ve doğrudan güneş ışığına maruz bırakılmamalıdır. \r\nEkim işleminden bir ay sonra ise Çiçek Coşturan bitki besini verilmelidir. Çiçek Coşturanın çiçek açan süs  bitkileri için bitki besini, sümbülünüz için gerekli olan tüm önemli besin maddelerini içermektedir. \r\nSonbahar ve kış mevsiminde verilen bitki besini sümbül soğanı tarafından depolanır. Böylece sümbülünüz baharda çiçeklenme dönemine daha dinç ve sağlıklı bir şekilde girer.', 3, 'resim/sumbul.jpg'),
(17, 'NERGİS', 'Bu bitkilerde sap 20-80 cm kadar yükselebilmektedir. Soğanlı olan bu \r\nbitkilerde taç yaprakları beyaz veya sarının karışımları şeklindedir.\r\nAnavatanı Avrupa olan bu bitkilerin en çok tür zenginliğine İspanya \r\nve Portekiz''de rastlanmaktadır. Ancak doğal olarak tüm Akdeniz kıyılarında, hatta bunun uzantısı olan Japonya''ya kadar aynı enlem dereceleri arasında görülmektedir. Dünyada Avrupa, Kuzey Amerika, Kuzey Afrika ülkelerinde tarımı yapılmaktadır.\r\nKökleri aynı soğana benzediğinden bazen yanlışlıkla yenmekte ve zehirlenmelere neden olmaktadır. Yaprakları direkt olarak soğandan çıkar. \r\nUzun ince bir şerit şeklinde, mavimsi yeşil renktedir. Çiçeklerinin kupa \r\nyaprakları yoktur ve onun yerini yan taç yapraklar almıştır. Yan taç yaprakların oval sarımsı beyaz renkte ve 6 adettir ve ana taç yaprak bir çan veya zurna şeklinde koyu sarı renkli,ucu dalgalı,ortada bir adet \r\ndöllenme tozluğu bulunur. Çiçekleri ayrı ayrı veya bir kök sap üzerinde şemsiye durumunda, beyaz veya sarı renktedir.', 3, 'resim/nergis.jpg'),
(18, 'LALE', 'Zambakgiller (Liliaceae) familyasından Tulipa cinsini oluşturan güzel çiçekleri ile süs bitkisi olarak yetiştirilen, soğanlı, çok yıllık ve otsu bitki türlerinin ortak adıdır.Gökkuşağının tüm renklerini içinde barındıran lale bitkisi bakımı kolay ve çok güzel bir çiçektir.\r\nAnavatanı Batı Asya olan lale ilk olarak Türkiye''de süs bitkisi olarak yetiştirilmiştir. Soğanlarının üzerinde zarımsı bir örtü bulunur. Etli ve yeşil 2-8 yaprağı vardır. Çiçekler, saplar ucunda çoğunlukla bir, bazen ikidir. Çiçek parçaları altılıdır. Kırmızı, sarı ve ara tonlarda renklere sahiptir. Beyaz \r\nen çok tercih edilen rengidir. Yaprakları uzun ve mızraksıdır.Sapının üstünde bir tek çiçek bulunur.\r\nÇiçekler çok çeşitli renklerde olduğu gibi alacalı da olabilir. Lale soğandan yetişir, ilkbaharda çiçek açar. Lale soğanları sonbaharda dikilmelidir. Sonbaharda dikilen lale soğanlarından yeni bitki çıktıktan sonra soğanlar çürür yerine yeni soğanlar meydana gelir. Kışın lale soğanları soğuktan kaçarak toprağın altına çekilirler. Her soğandan bir tek lale çıkar. Laleler katmerli ya da yalınkat olur. \r\nÇiçek gündüz güneş ışığının etkisiyle iyice açılır, akşamları yeniden toparlanır', 3, 'resim/lale.jpg'),
(19, 'KARDELEN', 'Kardelen çiçeği ülkemizde Kuzey ve Doğu Anadolu bölgesinde yetişen ve ilkbaharda görülen beyaz renkli sarkık çan biçimde çiçekler açan soğanlı yapıdaki bir bitki türüdür. Karlı günlerde çiçek verdiği kendini gösterdiği için “yılın ilk çiçeği” denen kardelen eski botanikçiler tarafından soğanlı menekşe olarak isimlendirilmiştir.\r\nBotanik biliminde verilen ismiyle Galanthus Nivalis yani kardelen kara benzeyen süt çiçeği olarak söylenmektedir. Ayrıca kardelen çiçeği sadece karda değil gerekli olan tüm şartlar sağlandığında su üzerinde bile çiçeklerini açabiliyor. İngiltere’nin tanınmış ve ünlü bahçelerinde sık sık görülen bu çiçek eski zamanlardaki bahçe düzenlemelerinin en önemli ve ayrılmaz bir parçasıydı. Soğuk ve karlı kış günlerinde bile boy gösterebilmesi, çiçek verebilmesi ve dayanıklılığı sayesinde peyzaj mimarlarının çiçek listesinde genellikle ilk sıralarda yer alıyordu.', 3, 'resim/kardelen.jpg'),
(20, 'AKŞAM SEFASI', 'Gecesefasıgiller familyasındandır. Anayurdu Amerika kıtasıdır. Çok yıllık bir bitki olduğu halde bir yıllık işlemi uygulanan, 80-100 cm. kadar boylanabilen otsu bitkidir. Aralarında bodurları da bulunan ve 25 kadar türü olan akşam sefaları geniş alanda yetiştirilirse çevresine yayılarak çalımsı bir görünüm kazanır. Bitkinin yumru oluşturan sağlam bir kök yapısı; yuvarlak kesitli, yeşil renkli, içi dolu ve dayanıklı bir gövdesi vardır. Yeşil renkli, derin damarlı ve ucuna doğru sivrilen yaprakları oval biçimlidir. Yaz ortalarından sonbahardaki ilk donlara kadar çiçek açan bitkinin bu çiçekleri beyaz, sarı, kırmızı, pembe renklerde; bazen çizgili ya da alacalı ve kimi türlerde hafif ama hoş kokulu olur. Akşam safası, çiçeklerini pek ilginç şekilde açar: Güneşli günlerde akşama doğru açtığı çiçekleri bütün gece açık kalır ve sabah güneşini görür görmez kapanır. Oysa bulutlu günlerde bitki çiçeklerini sabahleyin açar ve bütün gün çiçekli kalır. Kimi zaman bir bitkide iki ayrı renkte açan çiçekler görülür, işte bitki bu şekilde açtığı çiçeklerin her biriyle, karabiberinkilere benzeyen siyah renkli birer tohum verir. Bol bol açan çiçeğiyle ekildiği bahçeye renkli bir görünüm katan, özellikle tarhları ve duvar diplerini süsleyen akşam sefaları, yetiştirilmesi kolay ve arsız bir bitki türüdür.', 4, 'resim/aksam.jpg'),
(21, 'YILDIZ ÇİÇEĞİ', 'Yıldız çiçeği, Asteraceae familyasından Dahlia cinsini oluşturan yaz ve sonbahar mevsiminde çiçek açan, gövdesi çalı, kökleri yumru şeklinde bitki cinsi. Anavatanı Meksika olup bu ülkenin ulusal çiçeği olarak kabul edilmektedir.\r\nİspanyollar tarafından ilk olarak Meksika dağlarında bulunmuştur. 1872 senesinde bir kutu yıldız çiçeği Meksika''dan Hollanda''ya gönderilmiştir. Sadece tek bir bitki bu yolculukta canlı kalabildi.\r\nYıldız çiçekleri bazı kelebek (Lepidoptera) türlerinin larvaları tarafından besin olarak tüketilmektedir.\r\nYıldız çiçeğinin bilimsel adı Dahlia, 18. yüzyıl İsveç botanikçisi Anders Dahl adından gelmektedir. Yıldız çiçeği Washington Eyaletinin başkenti Seattle''nin resmi çiçeğidir.', 4, 'resim/yildiz.jpg');

