--
-- Tablo için tablo yapısı `cicekturu`
--

CREATE TABLE IF NOT EXISTS `cicekturu` (
  `turid` int(11) NOT NULL AUTO_INCREMENT,
  `turadi` text COLLATE utf8_turkish_ci NOT NULL,
  `turozellik` text COLLATE utf8_turkish_ci NOT NULL,
  PRIMARY KEY (`turid`)
) ENGINE=InnoDB  DEFAULT CHARSET=utf8 COLLATE=utf8_turkish_ci AUTO_INCREMENT=5 ;

--
-- Tablo döküm verisi `cicekturu`
--

INSERT INTO `cicekturu` (`turid`, `turadi`, `turozellik`) VALUES
(1, 'BİR YILLIK ÇİÇEKLER', 'Tohumundan ya da çiçeklerinden üretilerek yetiştirilen ve bir gelişim mevsimi sonunda bozulup ölen bitkilerdir. Bu tür bitkilerden dayanıklı olanları, doğrudan doğruya çiçek açacakları toprağa ilkbahar mevsiminde ekilir. Burada çimlenip gelişir, renkli çiçek tarhları ya da bordürleri oluşturup sonbahardaki ilk donlara kadar çiçek açmayı sürdürür. Bu grupta alissum, aynısefa çiçeği (kandil çiçeği), bir yıllık kasımpatılar, latin çiçeği, ayçiçeği, gelincik, muhabbet çiçeği vb''leri sayabiliriz. \r\nYarı dayanıklı çiçekler soğuk havada üretilemez. Bu nedenle tohumları ilkbaharda sıcak yastıklara ekilir. Burada çimlendirilip elde edilen fidelerinin bakımı sera gibi kapalı mekânlarda yapılır ve yaz başlarında tüpler içindeki fideleri alınıp bahçeye ekilir. Bu grup içinde hercaimenekşe, lobelya, tütün çiçeği, petunya, alev çiçeği, kadife çiçeği, bir yıllık karanfiller vb''leri sayılabilir. Bazı duyarlı bir yıllık çiçekler, yaz mevsiminde en sıcak zamanlarda yetiştirildikleri saksılar içinde bahçeye çıkarılabilir. Bu çiçekler arasında sinerelyaları sayabiliriz.'),
(2, 'ÇOK YILLIK ÇİÇEKLER', 'Çok yıllık bitki, yıldan yıla gelişip serpilerek uzun yıllar yaşayan ağaç, ağaççık, çalı ve dayanıklı otsu bitki vb. anlamına gelir. Bu türlerden konumuzu ilgilendiren çok yıllık dayanıklı otsu bitkiler, bahçelerimizde uzun yıllar yaşar. Her yıl kış mevsiminde gövde ve yaprakları ölürken kökleri dinlenmeye geçer. İlkbahar mevsimi gelince, bu türlerin toprak üstü kesimleri tüm canlılığıyla âdeta topraktan fışkırır gibi gelişir. Bu duruma istisna olup da kışın yaprakları ölmeyen karanfil, süsen vb. türler de vardır. Dayanıklı çok yıllık çiçekler böyle uzun yıllar yaşarken gülhatmi, keten çiçeği vb. gibi bazı türlerin ömrü yalnızca birkaç yıldır. Kimi çok yıllık çiçekler de yarı dayanıklıdır. Bu gibi türlerin kış mevsiminde korunması gerekir. Bu gruba, çok yıllık kasımpatılar ve sardunyalar girer. çok yıllık çiçek türlerinden oldukları halde ikinci yıl ve sonrasında bozuldukları için bir yıllık çiçek işlemi gören çiçek türleri de vardır. Bunlar arasında aslanağzı, nemezya ve petunya ile benzerlerini sayabiliriz.'),
(3, 'SOĞANLI ÇİÇEKLER', 'Bir bitkinin soğanı; yumurta biçiminde, bir ucu sivri öteki ucu yuvarlak olan ve söz konusu bitkinin besin deposunu oluşturan organıdır. Bu soğanın alt kısmında, bitkinin cücük de denilen içteki ''tomurcuğunun'' çevresini saran etli ve özlü tabakalar halindeki yaprakları bulunur. Tomurcuğun içinde, bitkinin toprak üstündeki yaprak, sap ve çiçeklerindeki özlerden vardır. Bu tomurcuk gelişmeye başlayınca, çevresindeki yaprakların etli ve özlü besinlerini kullanır. Bir yandan bitkinin toprak üstündeki gövde, yaprak, sap ve çiçeklerini oluştururken, bir yandan da toprağın derinliğine doğru uzayan köklerini meydana getirir. Bahçe çiçeklerinden en fazla tanınan dayanıklı soğanlı bitkilere örnek olarak zambakgiller familyasındaki kardelen, sümbül, beyaz zambak, nergis, lale vb''lerini sayabiliriz. Bazı soğanlı bitkiler ise yarı dayanıklı olur. Bunlar özellikle soğuk iklimlere karşı duyarlıdırlar. Bu türler arasında amarillis vb''leri sayılabilir'),
(4, 'YUMRULU ÇİÇEKLER', 'Yumrular, toprak altında bitkinin gövdesinin ya da kökünün şişip büyüyerek besin deposu haline gelen organlarıdır. Yumrular, içlerinde ''tomurcuk'' ya da cücük denilen kesimleriyle bitkinin gövde, sap, yaprak ve çiçekleri ile köklerini oluşturur. Çiçek bahçesindeki yumrulu bitkiler arasında akşam sefası, anemon (Manisa lalesi), dalya yıldızçiçeği, yumrulu begonyalar vb''leri sayılabilir.');

